  TokenNfts(
    input: {filter: {address: {_eq: "0xbc4ca0eda7647a8ab7c2061c2e118a18a936f13d"}, metaData: {attributes: {trait_type: {_eq: "Background"}, value: {_eq: "Orange"}}}}, blockchain: ethereum}
  ) {
    TokenNft {
      address
      tokenId
      token {
        name
        symbol
      }
      nftSaleTransactions {
        dappSlug
        formattedPaymentAmount
        formattedFeeAmountInUSDC
        formattedPaymentAmountInNativeToken
        paymentToken {
          address
          name
          decimals
          symbol
        }
        blockNumber
        blockTimestamp
        nfts {
          tokenId
          tokenType
        }
      }
      metaData {
        attributes {
          trait_type
          value
        }
      }
      contentType
      contentValue {
        image {
          extraSmall
          large
          medium
          original
        }
      }
    }
  }
}
```

